pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "./ArmadaNodes.sol";
import "./ArmadaProjects.sol";
import "./ArmadaRegistry.sol";
import "./ArmadaReservations.sol";
import "./ArmadaTypes.sol";

/// @title Entry point for managing network node operators
contract ArmadaOperators is AccessControlUpgradeable, PausableUpgradeable, ReentrancyGuardUpgradeable, UUPSUpgradeable {
  using EnumerableSet for EnumerableSet.Bytes32Set;

  // Controls who can do data import during contract initialization
  bytes32 public constant IMPORTER_ROLE = keccak256("IMPORTER_ROLE");

  ArmadaRegistry private _registry;
  uint256 private _stakePerNode; // Required stake to run one content node on the network

  mapping(bytes32 => ArmadaOperator) private _operators;
  EnumerableSet.Bytes32Set private _operatorIds;

  event OperatorCreated(bytes32 indexed operatorId, address indexed owner, string name, string email);
  event OperatorDeleted(bytes32 indexed operatorId, address indexed owner, string name, string email);
  event OperatorOwnerChanged(bytes32 indexed operatorId, address indexed oldOwner, address indexed newOwner);
  event OperatorPropsChanged(bytes32 indexed operatorId, string oldName, string oldEmail, string newName, string newEmail);
  event OperatorStakeChanged(bytes32 indexed operatorId, uint256 oldStake, uint256 newStake);
  event OperatorBalanceChanged(bytes32 indexed operatorId, uint256 oldBalance, uint256 newBalance);

  modifier onlyImpl {
    require(
      msg.sender == address(_registry.getBilling()) ||
      msg.sender == address(_registry.getNodes()) ||
      msg.sender == address(_registry.getProjects()) ||
      msg.sender == address(_registry.getReservations()),
      "not impl");
    _;
  }

  modifier onlyAdmin {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "not admin");
    _;
  }

  modifier onlyOperator(bytes32 operatorId) {
    ArmadaOperator storage operator = _operators[operatorId];
    require(operator.id != 0, "unknown operator");
    require(msg.sender == operator.owner, "not operator");
    _;
  }

  modifier onlyAdminOrOperator(bytes32 operatorId) {
    ArmadaOperator storage operator = _operators[operatorId];
    require(operator.id != 0, "unknown operator");
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender) ||
      msg.sender == operator.owner, "not admin or operator");
    _;
  }

  modifier whenNotReconciling() {
    _registry.requireNotReconciling();
    _;
  }

  function requireTopologyNode(bytes32 nodeId, address sender) public virtual {
    ArmadaNodes nodes = _registry.getNodes();
    ArmadaNode memory nodeCopy = nodes.getNode(nodeId);
    require(nodeCopy.topology, "not topology node");
    ArmadaOperator storage operator = _operators[nodeCopy.operatorId];
    assert(operator.id != 0); // Impossible because of getNode()
    require(sender != address(0), "zero sender");
    require(operator.owner == sender, "not operator");
  }

  /// @dev Called once to set up the contract. Not called during proxy upgrades.
  ///
  /// @param grantImporterRole allows the contract deployer to import initial data into
  /// the contract using unsafeImport* functions, which is used for proxy-less upgrades.
  /// CAUTION: Once import is finished, the importer role should be explicitly revoked.
  function initialize(address[] calldata admins, ArmadaRegistry registry, uint256 stakePerNode, bool grantImporterRole)
  public initializer {
    __Context_init();
    __ERC165_init();
    __ERC1967Upgrade_init();
    __AccessControl_init();
    __Pausable_init();
    __ReentrancyGuard_init();
    __UUPSUpgradeable_init();

    _registry = registry;
    _stakePerNode = stakePerNode;
    require(admins.length > 0, "no admins");
    for (uint256 i = 0; i < admins.length; ++i) {
      require(admins[i] != address(0), "zero admin");
      _grantRole(DEFAULT_ADMIN_ROLE, admins[i]);
    }

    if (grantImporterRole) {
      // solhint-disable-next-line avoid-tx-origin
      _grantRole(IMPORTER_ROLE, tx.origin); // Only the contract deployer will have this role
      _setRoleAdmin(IMPORTER_ROLE, IMPORTER_ROLE); // Admin must not be able to grant this role
    }
  }

  /// @dev Reverts if proxy upgrade of this contract by msg.sender is not allowed
  function _authorizeUpgrade(address) internal virtual override onlyAdmin {}

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetRegistry(ArmadaRegistry registry) public virtual onlyAdmin { _registry = registry; }

  /// @dev Allows to import initial contract data. Used for proxy-less upgrades.
  /// @param revokeImporterRole stops further data import by revoking the role.
  /// CAUTION: Once import is finished, the role should be explicitly revoked.
  function unsafeImportData(ArmadaOperator[] calldata operators, bool revokeImporterRole)
  public onlyRole(IMPORTER_ROLE) {
    for (uint256 i = 0; i < operators.length; i++) {
      _operators[operators[i].id] = operators[i];
      require(_operatorIds.add(operators[i].id), "duplicate id");
    }
    if (revokeImporterRole) {
      _revokeRole(IMPORTER_ROLE, msg.sender);
    }
  }

  /// @dev Adjusts multiple operators balances relative to their current values.
  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetBalances(uint256 skip, uint256 size, uint256 mul, uint256 div)
  public virtual onlyAdmin {
    require(mul != 0, "zero mul");
    uint256 length = _operatorIds.length();
    uint256 n = Math.min(size, length > skip ? length - skip : 0);
    for (uint256 i = 0; i < n; i++) {
      ArmadaOperator storage operator = _operators[_operatorIds.at(skip + i)];
      operator.balance = operator.balance * mul / div;
    }
  }

  function pause() public virtual onlyAdmin { _pause(); }
  function unpause() public virtual onlyAdmin { _unpause(); }

  function getRegistry() public virtual view returns (ArmadaRegistry) { return _registry; }
  function getStakePerNode() public virtual view returns (uint256) { return _stakePerNode; }
  function setStakePerNode(uint256 stake) public virtual onlyAdmin { _stakePerNode = stake; }

  function setOperatorBalanceImpl(bytes32 operatorId, uint256 decrease, uint256 increase)
  public virtual onlyImpl whenNotPaused {
    ArmadaOperator storage operator = _operators[operatorId];
    require(operator.id != 0, "unknown operator");
    operator.balance -= decrease;
    operator.balance += increase;
  }

  /// @notice Registers a new network operator. Only admin can do this.
  /// @dev Does not check name or email for validity or uniqueness
  function createOperator(address owner, string calldata name, string calldata email)
  public onlyAdmin whenNotReconciling whenNotPaused returns (bytes32 operatorId) {
    require(owner != address(0), "zero owner");
    require(bytes(name).length > 0, "empty name");
    require(bytes(name).length <= ARMADA_MAX_NAME_BYTES, "name too long");
    require(bytes(email).length <= ARMADA_MAX_EMAIL_BYTES, "email too long");
    operatorId = keccak256(abi.encodePacked(_registry.newNonceImpl()));
    _operators[operatorId] = ArmadaOperator({id: operatorId, owner: owner, name: name, email: email, stake: 0, balance: 0});
    assert(_operatorIds.add(operatorId));
    emit OperatorCreated(operatorId, owner, name, email);
  }

  /// @notice Unregisters a network operator. Reverts if operator has stake or nodes.
  function deleteOperator(bytes32 operatorId)
  public virtual onlyAdmin whenNotReconciling whenNotPaused {
    ArmadaOperator memory operatorCopy = _operators[operatorId];
    require(operatorCopy.id != 0, "unknown operator");
    ArmadaNodes nodes = _registry.getNodes();
    require(nodes.getNodeCount(operatorId, true) == 0, "operator has nodes");
    require(nodes.getNodeCount(operatorId, false) == 0, "operator has nodes");
    require(operatorCopy.stake == 0, "operator has stake");
    require(operatorCopy.balance == 0, "operator has balance");
    delete _operators[operatorId];
    assert(_operatorIds.remove(operatorId));
    emit OperatorDeleted(operatorId, operatorCopy.owner, operatorCopy.name, operatorCopy.email);
  }

  function setOperatorOwner(bytes32 operatorId, address owner)
  public virtual onlyAdminOrOperator(operatorId) whenNotReconciling whenNotPaused {
    require(owner != address(0), "zero owner");
    ArmadaOperator storage operator = _operators[operatorId];
    assert(operator.id != 0); // Impossible because of onlyAdminOrOperator
    address oldOwner = operator.owner;
    operator.owner = owner;
    emit OperatorOwnerChanged(operatorId, oldOwner, owner);
  }

  /// @dev Does not check name or email for validity or uniqueness
  function setOperatorProps(bytes32 operatorId, string calldata name, string calldata email)
  public virtual onlyOperator(operatorId) whenNotReconciling whenNotPaused {
    require(bytes(name).length > 0, "empty name");
    require(bytes(name).length <= ARMADA_MAX_NAME_BYTES, "name too long");
    require(bytes(email).length <= ARMADA_MAX_EMAIL_BYTES, "email too long");
    ArmadaOperator storage operator = _operators[operatorId];
    assert(operator.id != 0); // Impossible because of onlyOperator
    string memory oldName = operator.name;
    string memory oldEmail = operator.email;
    operator.name = name;
    operator.email = email;
    emit OperatorPropsChanged(operatorId, oldName, oldEmail, name, email);
  }

  /// @notice Transfers tokens into the contract and applies them toward given operator stake.
  /// @dev Needs either a token allowance from msg.sender, or a gasless approval (v/r/s != 0).
  /// @dev CAUTION: To avoid loss of funds, do NOT deposit to this contract by token.transfer().
  function depositOperatorStake(bytes32 operatorId, uint256 amount, uint256 deadline, uint8 v, bytes32 r, bytes32 s)
  public virtual whenNotPaused {
    ArmadaOperator storage operator = _operators[operatorId];
    require(operator.id != 0, "unknown operator");
    uint256 oldStake = operator.stake;
    operator.stake += amount;
    if (s != 0) {
      _registry.getToken().permit(msg.sender, address(this), amount, deadline, v, r, s);
    } else {
      require(deadline == 0 && v == 0 && r == 0, "invalid permit");
    }
    _registry.getToken().transferFrom(msg.sender, address(_registry), amount);
    emit OperatorStakeChanged(operatorId, oldStake, operator.stake);
  }

  /// @notice Transfers stake from contract to given recipient. Reverts if stake is locked.
  function withdrawOperatorStake(bytes32 operatorId, uint256 amount, address to)
  public virtual nonReentrant onlyOperator(operatorId) whenNotReconciling whenNotPaused {
    ArmadaOperator storage operator = _operators[operatorId];
    assert(operator.id != 0); // Impossible because of onlyOperator
    ArmadaNodes nodes = _registry.getNodes();
    uint256 lockedStake = nodes.getNodeCount(operatorId, false) * _stakePerNode;
    require(operator.stake - amount >= lockedStake, "not enough stake");
    uint256 oldStake = operator.stake;
    operator.stake -= amount;
    _registry.getToken().transferFrom(address(_registry), to, amount);
    emit OperatorStakeChanged(operatorId, oldStake, operator.stake);
  }

  /// @notice Transfers USDC into the contract and applies them toward given operator balance.
  /// @dev Needs either a token allowance from msg.sender, or a gasless approval (v/r/s != 0).
  /// @dev CAUTION: To avoid loss of funds, do NOT deposit to this contract by token.transfer().
  function depositOperatorBalance(bytes32 operatorId, uint256 amount, uint256 deadline, uint8 v, bytes32 r, bytes32 s)
  public virtual whenNotPaused {
    ArmadaOperator storage operator = _operators[operatorId];
    require(operator.id != 0, "unknown operator");
    uint256 oldBalance = operator.balance;
    operator.balance += amount;
    if (s != 0) {
      _registry.getUSDC().permit(msg.sender, address(this), amount, deadline, v, r, s);
    } else {
      require(deadline == 0 && v == 0 && r == 0, "invalid permit");
    }
    _registry.getUSDC().transferFrom(msg.sender, address(_registry), amount);
    emit OperatorBalanceChanged(operatorId, oldBalance, operator.balance);
  }

  /// @notice Transfers earned USDC from contract to given recipient.
  function withdrawOperatorBalance(bytes32 operatorId, uint256 amount, address to)
  public virtual nonReentrant onlyOperator(operatorId) whenNotReconciling whenNotPaused {
    ArmadaOperator storage operator = _operators[operatorId];
    assert(operator.id != 0); // Impossible because of onlyOperator
    require(operator.balance >= amount, "not enough balance");
    uint256 oldBalance = operator.balance;
    operator.balance -= amount;
    _registry.getUSDC().transferFrom(address(_registry), to, amount);
    emit OperatorBalanceChanged(operatorId, oldBalance, operator.balance);
  }

  /// @dev Reverts if the id is unknown
  function getOperator(bytes32 operatorId)
  public virtual view returns (ArmadaOperator memory) {
    ArmadaOperator storage operator = _operators[operatorId];
    require(operator.id != 0, "unknown operator");
    return operator;
  }

  function getOperatorCount()
  public virtual view returns (uint256) {
    return _operatorIds.length();
  }

  /// @dev Truncates the results if skip or size are out of bounds
  function getOperators(uint256 skip, uint256 size)
  public virtual view returns (ArmadaOperator[] memory values) {
    uint256 length = _operatorIds.length();
    uint256 n = Math.min(size, length > skip ? length - skip : 0);
    values = new ArmadaOperator[](n);
    for (uint256 i = 0; i < n; i++) {
      values[i] = _operators[_operatorIds.at(skip + i)];
    }
  }
}
","keccak256":"0x1c67b67afbd3d2ad0f70f07500de9aeba8054ca5b18043886906f7102b518bd2","license":"UNLICENSED"},"contracts/ArmadaProjects.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "./ArmadaNodes.sol";
import "./ArmadaOperators.sol";
import "./ArmadaRegistry.sol";
import "./ArmadaReservations.sol";
import "./ArmadaTypes.sol";

/// @title Entry point for managing projects that host content on the network
contract ArmadaProjects is AccessControlUpgradeable, PausableUpgradeable, ReentrancyGuardUpgradeable, UUPSUpgradeable {
  using EnumerableSet for EnumerableSet.Bytes32Set;

  // Controls who can do data import during contract initialization
  bytes32 public constant IMPORTER_ROLE = keccak256("IMPORTER_ROLE");

  // Project owners must first be allowed by admin before they can register projects
  bytes32 public constant PROJECT_CREATOR_ROLE = keccak256("PROJECT_CREATOR_ROLE");

  ArmadaRegistry private _registry;

  mapping(bytes32 => ArmadaProject) private _projects;
  EnumerableSet.Bytes32Set private _projectIds;

  event ProjectCreated(bytes32 indexed projectId, address indexed owner, string name, string email, string content, bytes32 checksum, string metadata);
  event ProjectDeleted(bytes32 indexed projectId, address indexed owner, string name, string email, string content, bytes32 checksum, string metadata);
  event ProjectOwnerChanged(bytes32 indexed projectId, address indexed oldOwner, address indexed newOwner);
  event ProjectPropsChanged(bytes32 indexed projectId, string oldName, string oldEmail, string newName, string newEmail);
  event ProjectEscrowChanged(bytes32 indexed projectId, uint256 oldEscrow, uint256 newEscrow);
  event ProjectContentChanged(bytes32 indexed projectId, string oldContent, bytes32 oldChecksum, string newContent, bytes32 newChecksum);
  event ProjectMetadataChanged(bytes32 indexed projectId, string oldMetadata, string newMetadata);

  modifier onlyImpl {
    require(
      msg.sender == address(_registry.getBilling()) ||
      msg.sender == address(_registry.getNodes()) ||
      msg.sender == address(_registry.getOperators()) ||
      msg.sender == address(_registry.getReservations()),
      "not impl");
    _;
  }

  modifier onlyAdmin {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "not admin");
    _;
  }

  modifier onlyProjectCreator {
    require(hasRole(PROJECT_CREATOR_ROLE, address(0)) ||
      hasRole(PROJECT_CREATOR_ROLE, msg.sender), "not project creator");
    _;
  }

  modifier onlyProjectOwner(bytes32 projectId) {
    ArmadaProject storage project = _projects[projectId];
    require(project.id != 0, "unknown project");
    require(msg.sender == project.owner, "not project owner");
    _;
  }

  modifier onlyAdminOrProjectOwner(bytes32 projectId) {
    ArmadaProject storage project = _projects[projectId];
    require(project.id != 0, "unknown project");
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender) ||
      msg.sender == project.owner, "not admin or project owner");
    _;
  }

  modifier whenNotReconciling() {
    _registry.requireNotReconciling();
    _;
  }

  /// @dev Called once to set up the contract. Not called during proxy upgrades.
  ///
  /// @param grantImporterRole allows the contract deployer to import initial data into
  /// the contract using unsafeImport* functions, which is used for proxy-less upgrades.
  /// CAUTION: Once import is finished, the importer role should be explicitly revoked.
  function initialize(address[] calldata admins, ArmadaRegistry registry, bool grantImporterRole)
  public initializer {
    __Context_init();
    __ERC165_init();
    __ERC1967Upgrade_init();
    __AccessControl_init();
    __Pausable_init();
    __ReentrancyGuard_init();
    __UUPSUpgradeable_init();

    _registry = registry;
    require(admins.length > 0, "no admins");
    for (uint256 i = 0; i < admins.length; ++i) {
      require(admins[i] != address(0), "zero admin");
      _grantRole(DEFAULT_ADMIN_ROLE, admins[i]);
    }

    if (grantImporterRole) {
      // solhint-disable-next-line avoid-tx-origin
      _grantRole(IMPORTER_ROLE, tx.origin); // Only the contract deployer will have this role
      _setRoleAdmin(IMPORTER_ROLE, IMPORTER_ROLE); // Admin must not be able to grant this role
    }
  }

  /// @dev Reverts if proxy upgrade of this contract by msg.sender is not allowed
  function _authorizeUpgrade(address) internal virtual override onlyAdmin {}

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetRegistry(ArmadaRegistry registry) public virtual onlyAdmin { _registry = registry; }

  /// @dev Allows to import initial contract data. Used for proxy-less upgrades.
  /// @param creators Adding the zero address will allow anyone to create projects.
  /// @param revokeImporterRole stops further data import by revoking the role.
  /// CAUTION: Once import is finished, the role should be explicitly revoked.
  function unsafeImportData(ArmadaProject[] calldata projects, address[] calldata creators, bool revokeImporterRole)
  public onlyRole(IMPORTER_ROLE) {
    for (uint256 i = 0; i < projects.length; i++) {
      _projects[projects[i].id] = projects[i];
      require(_projectIds.add(projects[i].id), "duplicate id");
    }
    for (uint256 i = 0; i < creators.length; i++) {
      _grantRole(PROJECT_CREATOR_ROLE, creators[i]);
    }
    if (revokeImporterRole) {
      _revokeRole(IMPORTER_ROLE, msg.sender);
    }
  }

  /// @dev Adjusts multiple projects escrows relative to their current values.
  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetEscrows(uint256 skip, uint256 size, uint256 mul, uint256 div)
  public virtual onlyAdmin {
    require(mul != 0, "zero mul");
    uint256 length = _projectIds.length();
    uint256 n = Math.min(size, length > skip ? length - skip : 0);
    for (uint256 i = 0; i < n; i++) {
      ArmadaProject storage project = _projects[_projectIds.at(skip + i)];
      project.escrow = project.escrow * mul / div;
      project.reserve = project.reserve * mul / div;
    }
  }

  function pause() public virtual onlyAdmin { _pause(); }
  function unpause() public virtual onlyAdmin { _unpause(); }

  function getRegistry() public virtual view returns (ArmadaRegistry) { return _registry; }

  function setProjectEscrowImpl(bytes32 projectId, uint256 decrease, uint256 increase)
  public virtual onlyImpl whenNotPaused {
    ArmadaProject storage project = _projects[projectId];
    require(project.id != 0, "unknown project");
    project.escrow -= decrease;
    project.escrow += increase;
  }

  function setProjectReserveImpl(bytes32 projectId, uint256 decrease, uint256 increase)
  public virtual onlyImpl whenNotPaused {
    ArmadaProject storage project = _projects[projectId];
    require(project.id != 0, "unknown project");
    project.reserve -= decrease;
    project.reserve += increase;
  }

  /// @notice Registers a new project. Requires project creator role.
  /// @dev Does not check name or email for validity or uniqueness
  function createProject(ArmadaCreateProjectData memory project)
  public onlyProjectCreator whenNotReconciling whenNotPaused returns (bytes32 projectId) {
    require(project.owner != address(0), "zero owner");
    require(bytes(project.name).length > 0, "empty name");
    require(bytes(project.name).length <= ARMADA_MAX_NAME_BYTES, "name too long");
    require(bytes(project.email).length <= ARMADA_MAX_EMAIL_BYTES, "email too long");
    require(bytes(project.content).length <= ARMADA_MAX_CONTENT_BYTES, "content too long");
    require(bytes(project.metadata).length <= ARMADA_MAX_METADATA_BYTES, "metadata too long");
    projectId = keccak256(abi.encodePacked(_registry.newNonceImpl()));
    _projects[projectId] = ArmadaProject({
      id: projectId, owner: project.owner, name: project.name, email: project.email, escrow: 0, reserve: 0,
      content: project.content, checksum: project.checksum, metadata: project.metadata
    });
    assert(_projectIds.add(projectId));
    emit ProjectCreated(projectId, project.owner, project.name, project.email, project.content, project.checksum, project.metadata);
  }

  /// @notice Unregisters a project. Reverts if project has escrow or reservations.
  function deleteProject(bytes32 projectId)
  public virtual onlyProjectOwner(projectId) whenNotReconciling whenNotPaused {
    ArmadaProject memory projectCopy = _projects[projectId];
    assert(projectCopy.id != 0); // Impossible because of onlyProjectOwner
    ArmadaReservations reservations = _registry.getReservations();
    require(reservations.getReservationCount(projectId) == 0, "project has reservations");
    require(projectCopy.escrow == 0, "project has escrow");
    delete _projects[projectId];
    assert(_projectIds.remove(projectId));
    emit ProjectDeleted(projectId, projectCopy.owner, projectCopy.name, projectCopy.email, projectCopy.content, projectCopy.checksum, projectCopy.metadata);
  }

  function setProjectOwner(bytes32 projectId, address owner)
  public virtual onlyAdminOrProjectOwner(projectId) whenNotReconciling whenNotPaused {
    require(owner != address(0), "zero owner");
    ArmadaProject storage project = _projects[projectId];
    assert(project.id != 0); // Impossible because of onlyAdminOrProjectOwner
    address oldOwner = project.owner;
    project.owner = owner;
    emit ProjectOwnerChanged(projectId, oldOwner, owner);
  }

  /// @dev Does not check name or email for validity or uniqueness
  function setProjectProps(bytes32 projectId, string calldata name, string calldata email)
  public virtual onlyProjectOwner(projectId) whenNotReconciling whenNotPaused {
    require(bytes(name).length > 0, "empty name");
    require(bytes(name).length <= ARMADA_MAX_NAME_BYTES, "name too long");
    require(bytes(email).length <= ARMADA_MAX_EMAIL_BYTES, "email too long");
    ArmadaProject storage project = _projects[projectId];
    assert(project.id != 0); // Impossible because of onlyProjectOwner
    string memory oldName = project.name;
    string memory oldEmail = project.email;
    project.name = name;
    project.email = email;
    emit ProjectPropsChanged(projectId, oldName, oldEmail, name, email);
  }

  /// @notice Publishes new content on the network
  function setProjectContent(bytes32 projectId, string calldata content, bytes32 checksum)
  public virtual onlyProjectOwner(projectId) whenNotReconciling whenNotPaused {
    require(bytes(content).length <= ARMADA_MAX_CONTENT_BYTES, "content too long");
    ArmadaProject storage project = _projects[projectId];
    assert(project.id != 0); // Impossible because of onlyProjectOwner
    string memory oldContent = project.content;
    bytes32 oldChecksum = project.checksum;
    project.content = content;
    project.checksum = checksum;
    emit ProjectContentChanged(projectId, oldContent, oldChecksum, content, checksum);
  }

  /// @notice Sets project metadata
  function setProjectMetadata(bytes32 projectId, string calldata metadata)
  public virtual onlyProjectOwner(projectId) whenNotReconciling whenNotPaused {
    require(bytes(metadata).length <= ARMADA_MAX_METADATA_BYTES, "metadata too long");
    ArmadaProject storage project = _projects[projectId];
    assert(project.id != 0); // Impossible because of onlyProjectOwner
    string memory oldMetadata = project.metadata;
    project.metadata = metadata;
    emit ProjectMetadataChanged(projectId, oldMetadata, project.metadata);
  }

  /// @notice Transfers USDC into the contract and applies them toward given operator stake.
  /// @dev Needs either a token allowance from msg.sender, or a gasless approval (v/r/s != 0).
  /// @dev CAUTION: To avoid loss of funds, do NOT deposit to this contract by token.transfer().
  function depositProjectEscrow(bytes32 projectId, uint256 amount, uint256 deadline, uint8 v, bytes32 r, bytes32 s)
  public virtual whenNotPaused {
    ArmadaProject storage project = _projects[projectId];
    require(project.id != 0, "unknown project");
    uint256 oldEscrow = project.escrow;
    project.escrow += amount;
    if (s != 0) {
      _registry.getUSDC().permit(msg.sender, address(this), amount, deadline, v, r, s);
    } else {
      require(deadline == 0 && v == 0 && r == 0, "invalid permit");
    }
    _registry.getUSDC().transferFrom(msg.sender, address(_registry), amount);
    emit ProjectEscrowChanged(projectId, oldEscrow, project.escrow);
  }

  /// @notice Transfers escrow from contract to given recipient. Reverts if escrow is reserved.
  function withdrawProjectEscrow(bytes32 projectId, uint256 amount, address to)
  public virtual nonReentrant onlyProjectOwner(projectId) whenNotReconciling whenNotPaused {
    ArmadaProject storage project = _projects[projectId];
    assert(project.id != 0); // Impossible because of onlyProjectOwner
    require(project.escrow >= project.reserve + amount, "not enough escrow");
    uint256 oldEscrow = project.escrow;
    project.escrow -= amount;
    _registry.getUSDC().transferFrom(address(_registry), to, amount);
    emit ProjectEscrowChanged(projectId, oldEscrow, project.escrow);
  }

  /// @dev Reverts if the id is unknown
  function getProject(bytes32 projectId)
  public virtual view returns (ArmadaProject memory) {
    ArmadaProject memory project = _projects[projectId];
    require(project.id != 0, "unknown project");
    return project;
  }

  function getProjectCount()
  public virtual view returns (uint256) {
    return _projectIds.length();
  }

  /// @dev Truncates the results if skip or size are out of bounds
  function getProjects(uint256 skip, uint256 size)
  public virtual view returns (ArmadaProject[] memory values) {
    uint256 length = _projectIds.length();
    uint256 n = Math.min(size, length > skip ? length - skip : 0);
    values = new ArmadaProject[](n);
    for (uint256 i = 0; i < n; i++) {
      values[i] = _projects[_projectIds.at(skip + i)];
    }
  }
}
","keccak256":"0x365d6e4d6373c20e2b50e3b650054625ba7aaf06d6e624e430b156f4187d41d0","license":"UNLICENSED"},"contracts/ArmadaRegistry.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
// solhint-disable not-rely-on-time
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";

import "./ArmadaBilling.sol";
import "./ArmadaNodes.sol";
import "./ArmadaOperators.sol";
import "./ArmadaProjects.sol";
import "./ArmadaReservations.sol";
import "./ArmadaToken.sol";

/// @title Main entry point for the core contracts and functionality
contract ArmadaRegistry is AccessControlUpgradeable, PausableUpgradeable, ReentrancyGuardUpgradeable, UUPSUpgradeable {
  // Controls who in addition to topology nodes can reconcile the network. Grant to zero address to allow everybody.
  bytes32 public constant RECONCILER_ROLE = keccak256("RECONCILER_ROLE");

  string private _version;          // Interpreted by the node software, used for automatic upgrading of the nodes
  uint256 private _nonce;           // Auto-incremented value used for generating unique ids
  uint256 private _lastEpochStart;  // Timestamp of the last epoch start in seconds since 1/1/1970 midnight UTC
  uint256 private _lastEpochLength; // Duration of the last epoch in seconds
  uint256 private _nextEpochLength; // Duration of the next epoch in seconds
  uint256 private _cuedEpochLength; // Pending change of the epoch duration, becomes effective after the next epoch
  uint256 private _gracePeriod;     // Period in seconds at the end of the epoch when node prices can't change

  ERC20Permit private _usdc;
  ArmadaToken private _token;
  ArmadaBilling private _billing;
  ArmadaNodes private _nodes;
  ArmadaOperators private _operators;
  ArmadaProjects private _projects;
  ArmadaReservations private _reservations;

  event EpochAdvanced(uint256 epochStart);

  modifier onlyImpl {
    require(
      msg.sender == address(_nodes) ||
      msg.sender == address(_operators) ||
      msg.sender == address(_projects) ||
      msg.sender == address(_reservations),
      "not impl");
    _;
  }

  modifier onlyAdmin {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "not admin");
    _;
  }

  modifier onlyReconcilerOrTopologyNode(bytes32 nodeIdOrZero) {
    if (nodeIdOrZero == 0) {
      require(
        hasRole(RECONCILER_ROLE, address(0)) ||
        hasRole(RECONCILER_ROLE, msg.sender),
        "not reconciler");
    } else {
      _operators.requireTopologyNode(nodeIdOrZero, msg.sender);
    }
    _;
  }

  modifier whenReconciling() {
    requireReconciling();
    _;
  }

  /// @dev Called once to set up the contract. Not called during proxy upgrades.
  function initialize(address[] calldata admins, ArmadaRegistryInitializeData calldata data)
  public initializer {
    __Context_init();
    __ERC165_init();
    __ERC1967Upgrade_init();
    __AccessControl_init();
    __Pausable_init();
    __ReentrancyGuard_init();
    __UUPSUpgradeable_init();

    require(data.usdc != data.token, "reused token");
    require(data.epochStart <= block.timestamp, "late epoch start");
    require(data.lastEpochLength != 0, "zero last epoch length");
    require(data.nextEpochLength != 0, "zero next epoch length");

    _version = data.version;
    _nonce = data.nonce;
    _lastEpochStart = data.epochStart;
    _lastEpochLength = data.lastEpochLength;
    _nextEpochLength = data.nextEpochLength;
    _cuedEpochLength = data.nextEpochLength;
    _gracePeriod = data.gracePeriod;

    _usdc = data.usdc;
    _token = data.token;
    _billing = data.billing;
    _nodes = data.nodes;
    _operators = data.operators;
    _projects = data.projects;
    _reservations = data.reservations;

    require(admins.length > 0, "no admins");
    for (uint256 i = 0; i < admins.length; ++i) {
      require(admins[i] != address(0), "zero admin");
      _grantRole(DEFAULT_ADMIN_ROLE, admins[i]);
    }

    if (address(_operators) != address(0)) {
      _usdc.approve(address(_operators), type(uint256).max);
      _token.approve(address(_operators), type(uint256).max);
    }
    if (address(_projects) != address(0)) {
      _usdc.approve(address(_projects), type(uint256).max);
    }
  }

  /// @dev Reverts if proxy upgrade of this contract by msg.sender is not allowed
  function _authorizeUpgrade(address) internal virtual override onlyAdmin {}

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetUSDC(ERC20Permit usdc) public virtual onlyAdmin {
    require(usdc != _token, "reused token");
    if (address(_operators) != address(0)) {
      _usdc.approve(address(_operators), 0);
      usdc.approve(address(_operators), type(uint256).max);
    }
    if (address(_projects) != address(0)) {
      _usdc.approve(address(_projects), 0);
      usdc.approve(address(_projects), type(uint256).max);
    }
    _usdc = usdc;
  }

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetToken(ArmadaToken token) public virtual onlyAdmin {
    require(token != _usdc, "reused token");
    if (address(_operators) != address(0)) {
      _token.approve(address(_operators), 0);
      token.approve(address(_operators), type(uint256).max);
    }
    _token = token;
  }

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetOperators(ArmadaOperators operators) public virtual onlyAdmin {
    if (address(_operators) != address(0)) {
      _usdc.approve(address(_operators), 0);
      _token.approve(address(_operators), 0);
    }
    if (address(operators) != address(0)) {
      _usdc.approve(address(operators), type(uint256).max);
      _token.approve(address(operators), type(uint256).max);
    }
    _operators = operators;
  }

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetProjects(ArmadaProjects projects) public virtual onlyAdmin {
    if (address(_projects) != address(0)) {
      _usdc.approve(address(_projects), 0);
    }
    if (address(projects) != address(0)) {
      _usdc.approve(address(projects), type(uint256).max);
    }
    _projects = projects;
  }

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetBilling(ArmadaBilling billing) public virtual onlyAdmin { _billing = billing; }
  function unsafeSetNodes(ArmadaNodes nodes) public virtual onlyAdmin { _nodes = nodes; }
  function unsafeSetReservations(ArmadaReservations reservations) public virtual onlyAdmin { _reservations = reservations; }

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  /// @dev One can unsafe-deposit funds to this contract directly by token.transfer().
  function unsafeWithdrawUSDC(address to, uint256 amount) public onlyAdmin { _usdc.transfer(to, amount); }
  function unsafeWithdrawToken(address to, uint256 amount) public onlyAdmin { _token.transfer(to, amount); }

  function pause() public virtual onlyAdmin { _pause(); }
  function unpause() public virtual onlyAdmin { _unpause(); }

  function getVersion() public virtual view returns (string memory) { return _version; }
  function setVersion(string calldata version) public virtual onlyAdmin { _version = version; }

  function getNonce() public virtual view returns (uint256) { return _nonce; }
  function newNonceImpl() public virtual onlyImpl returns (uint256) { return _nonce++; }

  function getUSDC() public virtual view returns (ERC20Permit) { return _usdc; }
  function getToken() public virtual view returns (ArmadaToken) { return _token; }
  function getBilling() public virtual view returns (ArmadaBilling) { return _billing; }
  function getNodes() public virtual view returns (ArmadaNodes) { return _nodes; }
  function getOperators() public virtual view returns (ArmadaOperators) { return _operators; }
  function getProjects() public virtual view returns (ArmadaProjects) { return _projects; }
  function getReservations() public virtual view returns (ArmadaReservations) { return _reservations; }

  function getLastEpochStart() public virtual view returns (uint256) { return _lastEpochStart; }
  function unsafeSetLastEpochStart(uint256 start) public virtual onlyAdmin { _lastEpochStart = start; } // Used by tests
  function getLastEpochLength() public virtual view returns (uint256) { return _lastEpochLength; }
  function getNextEpochLength() public virtual view returns (uint256) { return _nextEpochLength; }
  function getCuedEpochLength() public virtual view returns (uint256) { return _cuedEpochLength; }
  function setCuedEpochLength(uint256 length) public virtual onlyAdmin { _cuedEpochLength = length; }

  /// @return Number of seconds left in the current epoch
  function getEpochRemainder() public virtual view returns (uint256) {
    uint256 epochEnd = _lastEpochStart + _lastEpochLength;
    return epochEnd > block.timestamp ? epochEnd - block.timestamp : 0;
  }

  function getGracePeriod() public virtual view returns (uint256) { return _gracePeriod; }
  function setGracePeriod(uint256 period) public virtual onlyAdmin { _gracePeriod = period; }

  function requireReconciling() public virtual {
    require(block.timestamp >= _lastEpochStart + _lastEpochLength, "not reconciling");
  }

  function requireNotReconciling() public virtual {
    require(block.timestamp < _lastEpochStart + _lastEpochLength, "reconciling");
  }

  function requireNotGracePeriod() public virtual {
    require(block.timestamp + _gracePeriod < _lastEpochStart + _lastEpochLength, "grace period");
  }

  /// @notice Called by the leader topology node to finish epoch reconciliation and unfreeze the network state.
  /// @param topologyNodeId The topology node calling this function (zero is OK if caller has reconciler role)
  ///
  /// The core contracts automatically enter reconciliation mode when the last epoch ends. During reconciliation,
  /// the operations that change contract state, such as node pricing or reservations, are disallowed and will revert.
  /// The leader topology node is expected to call processBilling(), processRenewal(), and advanceEpoch() in this
  /// order, to execute reconcilication. Calling advanceEpoch() completes reconciliation and unfreezes the contracts.
  /// The reconciliation process should normally only take a few blocks.
  function advanceEpoch(bytes32 topologyNodeId)
  public virtual onlyReconcilerOrTopologyNode(topologyNodeId) whenReconciling whenNotPaused {
    uint256 nodeCount = _nodes.getNodeCount(0, false);
    require(_billing.getBillingNodeIndex() == nodeCount, "billing in progress");
    require(_billing.getRenewalNodeIndex() == nodeCount, "renewal in progress");
    _billing.setBillingNodeIndexImpl(0);
    _billing.setRenewalNodeIndexImpl(0);
    _lastEpochStart += _lastEpochLength;
    _lastEpochLength = _nextEpochLength;
    _nextEpochLength = _cuedEpochLength;
    emit EpochAdvanced(_lastEpochStart);
  }
}
","keccak256":"0x1fb339609533fa765a7fb2a80e12b1d121acf9b55ed68b2cd6b1eeb7aee09fbd","license":"UNLICENSED"},"contracts/ArmadaReservations.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

import "./ArmadaNodes.sol";
import "./ArmadaProjects.sol";
import "./ArmadaRegistry.sol";
import "./ArmadaTypes.sol";

/// @title Entry point for managing node reservations by projects
contract ArmadaReservations is AccessControlUpgradeable, PausableUpgradeable, UUPSUpgradeable {
  using EnumerableSet for EnumerableSet.Bytes32Set;

  // Controls who can do data import during contract initialization
  bytes32 public constant IMPORTER_ROLE = keccak256("IMPORTER_ROLE");

  ArmadaRegistry private _registry;

  mapping(bytes32 => EnumerableSet.Bytes32Set) private _projectNodeIds; // Union of last and next epoch

  event ReservationCreated(bytes32 indexed nodeId, bytes32 indexed operatorId, bytes32 indexed projectId,
    uint256 lastPrice, uint256 nextPrice, ArmadaSlot slot);
  event ReservationDeleted(bytes32 indexed nodeId, bytes32 indexed operatorId, bytes32 indexed projectId,
    uint256 lastPrice, uint256 nextPrice, ArmadaSlot slot);

  modifier onlyImpl {
    require(
      msg.sender == address(_registry) ||
      msg.sender == address(_registry.getBilling()) ||
      msg.sender == address(_registry.getNodes()) ||
      msg.sender == address(_registry.getOperators()) ||
      msg.sender == address(_registry.getProjects()),
      "not impl");
    _;
  }

  modifier onlyAdmin {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "not admin");
    _;
  }

  modifier onlyProjectOwner(bytes32 projectId) {
    ArmadaProjects projects = _registry.getProjects();
    ArmadaProject memory project = projects.getProject(projectId);
    require(msg.sender == project.owner, "not project owner");
    _;
  }

  modifier onlyAdminOrProjectOwner(bytes32 projectId) {
    ArmadaProjects projects = _registry.getProjects();
    ArmadaProject memory project = projects.getProject(projectId);
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender) ||
      msg.sender == project.owner, "not admin or project owner");
    _;
  }

  modifier whenNotReconciling() {
    _registry.requireNotReconciling();
    _;
  }

  /// @dev Called once to set up the contract. Not called during proxy upgrades.
  ///
  /// @param grantImporterRole allows the contract deployer to import initial data into
  /// the contract using unsafeImport* functions, which is used for proxy-less upgrades.
  /// CAUTION: Once import is finished, the importer role should be explicitly revoked.
  function initialize(address[] calldata admins, ArmadaRegistry registry, bool grantImporterRole)
  public initializer {
    __Context_init();
    __ERC165_init();
    __ERC1967Upgrade_init();
    __AccessControl_init();
    __Pausable_init();
    __UUPSUpgradeable_init();

    _registry = registry;
    require(admins.length > 0, "no admins");
    for (uint256 i = 0; i < admins.length; ++i) {
      require(admins[i] != address(0), "zero admin");
      _grantRole(DEFAULT_ADMIN_ROLE, admins[i]);
    }

    if (grantImporterRole) {
      // solhint-disable-next-line avoid-tx-origin
      _grantRole(IMPORTER_ROLE, tx.origin); // Only the contract deployer will have this role
      _setRoleAdmin(IMPORTER_ROLE, IMPORTER_ROLE); // Admin must not be able to grant this role
    }
  }

  /// @dev Reverts if proxy upgrade of this contract by msg.sender is not allowed
  function _authorizeUpgrade(address) internal virtual override onlyAdmin {}

  /// @dev CAUTION: This can break data consistency. Used for proxy-less upgrades.
  function unsafeSetRegistry(ArmadaRegistry registry) public virtual onlyAdmin { _registry = registry; }

  /// @dev Allows to import initial contract data. Used for proxy-less upgrades.
  /// @param revokeImporterRole stops further data import by revoking the role.
  /// CAUTION: Once import is finished, the role should be explicitly revoked.
  function unsafeImportData(ArmadaNode[] calldata nodes, bool revokeImporterRole)
  public onlyRole(IMPORTER_ROLE) {
    for (uint256 i = 0; i < nodes.length; i++) {
      bytes32 nodeId = nodes[i].id;
      if (nodes[i].projectIds[0] != 0)
        _projectNodeIds[nodes[i].projectIds[0]].add(nodeId);
      if (nodes[i].projectIds[1] != 0)
        _projectNodeIds[nodes[i].projectIds[1]].add(nodeId);
    }
    if (revokeImporterRole) {
      _revokeRole(IMPORTER_ROLE, msg.sender);
    }
  }

  function pause() public virtual onlyAdmin { _pause(); }
  function unpause() public virtual onlyAdmin { _unpause(); }

  function getRegistry() public virtual view returns (ArmadaRegistry) { return _registry; }

  function removeProjectNodeIdImpl(bytes32 projectId, bytes32 nodeId)
  public virtual onlyImpl whenNotPaused returns (bool) {
    return _projectNodeIds[projectId].remove(nodeId);
  }

  /// @notice Reserves content nodes and locks corresponding escrow amount. Reverts if escrow is insufficient.
  /// @param maxPrices At what max price to reserve. A safety mechanism in case price changes during this call.
  ///
  /// Use slot.last to reserve immediately in the current epoch (AKA spot), instead of at the start of next epoch.
  /// In this case, the node prices will be prorated, but the project won't be able to immediately release these
  /// nodes until next epoch. Use slot.next to auto-renew reservation in the next epoch (required if !slot.last).
  function createReservations(
    bytes32 projectId, bytes32[] memory nodeIds, uint256[] memory maxPrices, ArmadaSlot calldata slot)
  public virtual onlyProjectOwner(projectId) whenNotReconciling whenNotPaused {
    require(slot.last || slot.next, "no slot");
    require(nodeIds.length == maxPrices.length, "length mismatch");
    ArmadaNodes allNodes = _registry.getNodes();
    ArmadaProjects projects = _registry.getProjects();
    uint256 epochRemainder = _registry.getEpochRemainder();
    for (uint256 i = 0; i < nodeIds.length; i++) {
      ArmadaNode memory nodeCopy = allNodes.getNode(nodeIds[i]);
      createReservationImpl(allNodes, projects, projectId, nodeCopy, epochRemainder, maxPrices[i], slot);
    }
    ArmadaProject memory projectCopy = projects.getProject(projectId);
    require(projectCopy.escrow >= projectCopy.reserve, "not enough escrow");
  }

  function createReservationImpl(
    ArmadaNodes allNodes, ArmadaProjects projects, bytes32 projectId, ArmadaNode memory nodeCopy,
    uint256 epochRemainder, uint256 maxPrice, ArmadaSlot calldata slot)
  internal virtual {
    require(!nodeCopy.topology, "topology node");
    require(!nodeCopy.disabled, "node disabled");
    uint256 lastPrice = 0;
    uint256 nextPrice = 0;
    if (slot.last) {
      require(nodeCopy.projectIds[ARMADA_LAST_EPOCH] == 0, "node reserved");
      lastPrice = nodeCopy.prices[ARMADA_LAST_EPOCH];
      require(lastPrice <= maxPrice, "price mismatch");
      uint256 proratedPrice = lastPrice * epochRemainder / _registry.getLastEpochLength(); 
      allNodes.setNodeProjectImpl(nodeCopy.id, ARMADA_LAST_EPOCH, projectId);
      allNodes.setNodePriceImpl(nodeCopy.id, ARMADA_LAST_EPOCH, proratedPrice);
      projects.setProjectReserveImpl(projectId, 0, proratedPrice);
    }
    if (slot.next) {
      require(nodeCopy.projectIds[ARMADA_NEXT_EPOCH] == 0, "node reserved");
      nextPrice = nodeCopy.prices[ARMADA_NEXT_EPOCH];
      require(nextPrice <= maxPrice, "price mismatch");
      allNodes.setNodeProjectImpl(nodeCopy.id, ARMADA_NEXT_EPOCH, projectId);
      projects.setProjectReserveImpl(projectId, 0, nextPrice);
    }
    _projectNodeIds[projectId].add(nodeCopy.id);
    emit ReservationCreated(nodeCopy.id, nodeCopy.operatorId, projectId, lastPrice, nextPrice, slot);
  }

  /// @notice Releases content nodes starting from next epoch and unlocks project escrow
  ///
  /// Project owner can use slot.next to schedule reservation to be deleted in the next epoch.
  /// Admin can additionally use slot.last to immediately delete reservation in the current epoch.
  /// The latter is an emergency mechanism. As a side effect, the project won't be billed for the
  /// node usage in the epoch so far, and the operator won't be credited for providing this node.
  /// Moreover, the current epoch price of the released node will be set to its next epoch price,
  /// so if there was a price change scheduled by the operator, it will be accelerated to happen
  /// in this epoch.
  function deleteReservations(bytes32 projectId, bytes32[] memory nodeIds, ArmadaSlot calldata slot)
  public virtual onlyAdminOrProjectOwner(projectId) whenNotReconciling whenNotPaused {
    require(slot.last || slot.next, "no slot");
    require(!slot.last || hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "not admin");
    ArmadaNodes allNodes = _registry.getNodes();
    ArmadaProjects projects = _registry.getProjects();
    for (uint256 i = 0; i < nodeIds.length; i++) {
      bytes32 nodeId = nodeIds[i];
      ArmadaNode memory nodeCopy = allNodes.getNode(nodeId);
      deleteReservationImpl(allNodes, projects, projectId, nodeCopy, slot);
    }
  }

  function deleteReservationImpl(
    ArmadaNodes allNodes, ArmadaProjects projects, bytes32 projectId, bytes32 nodeId, ArmadaSlot calldata slot)
  public virtual onlyImpl whenNotPaused {
    ArmadaNode memory nodeCopy = allNodes.getNode(nodeId);
    deleteReservationImpl(allNodes, projects, projectId, nodeCopy, slot);
  }

  function deleteReservationImpl(
    ArmadaNodes allNodes, ArmadaProjects projects, bytes32 projectId, ArmadaNode memory nodeCopy,
    ArmadaSlot calldata slot)
  internal virtual {
    uint256 lastPrice = 0;
    uint256 nextPrice = 0;
    if (slot.last) {
      lastPrice = nodeCopy.prices[ARMADA_LAST_EPOCH];
      nextPrice = nodeCopy.prices[ARMADA_NEXT_EPOCH];
      require(nodeCopy.projectIds[ARMADA_LAST_EPOCH] == projectId, "node not reserved");
      allNodes.setNodePriceImpl(nodeCopy.id, ARMADA_LAST_EPOCH, nextPrice);
      allNodes.setNodeProjectImpl(nodeCopy.id, ARMADA_LAST_EPOCH, 0);
      projects.setProjectReserveImpl(projectId, lastPrice, 0);
      if (nodeCopy.projectIds[ARMADA_NEXT_EPOCH] != projectId) {
        assert(_projectNodeIds[projectId].remove(nodeCopy.id));
      }
    }
    if (slot.next) {
      nextPrice = nodeCopy.prices[ARMADA_NEXT_EPOCH];
      require(nodeCopy.projectIds[ARMADA_NEXT_EPOCH] == projectId, "node not reserved");
      allNodes.setNodeProjectImpl(nodeCopy.id, ARMADA_NEXT_EPOCH, 0);
      projects.setProjectReserveImpl(projectId, nextPrice, 0);
      if (nodeCopy.projectIds[ARMADA_LAST_EPOCH] != projectId) {
        assert(_projectNodeIds[projectId].remove(nodeCopy.id));
      }
    }
    if (slot.last && slot.next) {
      assert(_projectNodeIds[projectId].remove(nodeCopy.id));
    }
    emit ReservationDeleted(nodeCopy.id, nodeCopy.operatorId, projectId, lastPrice, nextPrice, slot);
  }

  function getReservationCount(bytes32 projectId)
  public virtual view returns (uint256) {
    return _projectNodeIds[projectId].length();
  }

  /// @dev Truncates the results if skip or size are out of bounds
  function getReservations(bytes32 projectId, uint256 skip, uint256 size)
  public virtual view returns (ArmadaNode[] memory result) {
    ArmadaNodes allNodes = _registry.getNodes();
    EnumerableSet.Bytes32Set storage nodeIds = _projectNodeIds[projectId];
    uint256 length = nodeIds.length();
    uint256 n = Math.min(size, length > skip ? length - skip : 0);
    result = new ArmadaNode[](n);
    for (uint256 i = 0; i < n; i++) {
      result[i] = allNodes.getNode(nodeIds.at(skip + i));
    }
  }
}
